  //         releaseDate: movieData.release_date
  //       };
  //     });
  //     setMovies(data.results);
  //   });
  // }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler} >Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
